/* cell structure */
typedef struct s7_cell {
  union {
    uint64_t flag;                /* type info */
    uint8_t type_field;
    uint16_t sflag;
  } tf;
  int64_t hloc;                   /* heap location */
  union {

    union {                       /* integers, floats */
      s7_int integer_value;
      s7_double real_value;

      struct {
	char padding[PRINT_NAME_PADDING];
	char name[PRINT_NAME_SIZE + 2];
      } pval;

      struct {                    /* ratios */
	s7_int numerator;
	s7_int denominator;
      } fraction_value;

      struct {                    /* complex numbers */
	s7_double rl;
	s7_double im;
      } complex_value;

      unsigned long ul_value;    /* these two are not used by s7 in any way -- "unsigned long" for backwards compatibility */
      uint64_t ull_value;

#if WITH_GMP
      mpz_t big_integer;          /* bignums */
      mpq_t big_ratio;
      mpfr_t big_real;
      mpc_t big_complex;
      /* using free_lists here was not faster, and avoiding the extra init/clear too tricky.  These make up
       *   no more than ca. 5% of the gmp computation -- it is totally dominated by stuff like __gmpz_mul,
       *   so I can't see much point in optimizing the background noise.  In a very numerical context,
       *   gmp slows us down by a factor of 50.
       */
#endif
    } number;

    struct {                       /* ports */
      port_t *port;
      unsigned char *data;
      uint32_t size, point;        /* these limit the in-core portion of a string-port to 2^32-1 bytes */
      uint32_t line_number, file_number;
      bool is_closed;
      port_type_t ptype;
    } prt;

    struct{                        /* characters */
      unsigned char c, up_c;
      int32_t length;
      bool alpha_c, digit_c, space_c, upper_c, lower_c;
      char c_name[12];
    } chr;

    struct {                       /* c-pointers */
      void *c_pointer; 
      s7_pointer c_type, info;
      /* if a gc_free function were included, the pointer could be freed (etc) via the GC-cache (sweep function), but then
       *   every possible explicit free function would need to warn the GC not to try to free the pointer.
       *   How to recognize an unfreed pointer at sweep time?
       */
    } cptr;

    int32_t baffle_key;            /* baffles */

    struct {                       /* vectors */
      s7_int length;
      union {
	s7_pointer *objects;
	s7_int *ints;
	s7_double *floats;
      } elements;
      vdims_t *dim_info;
      s7_pointer (*vget)(s7_scheme *sc, s7_pointer vec, s7_int loc);
      s7_pointer (*vset)(s7_scheme *sc, s7_pointer vec, s7_int loc, s7_pointer val);
    } vector;

    struct {                        /* stacks (internal) */
      s7_int length;
      s7_pointer *objects;
      vdims_t *dim_info;
      int64_t top;
    } stk;

    struct {                        /* hash-tables */
      uint32_t mask, entries;
      hash_entry_t **elements;
      hash_check_t hash_func;
      hash_map_t *loc;
      s7_pointer dproc;             /* user-supplied list of hashing functions */
    } hasher;

    struct {                        /* iterators */
      s7_pointer obj, cur;
      union {
	s7_int loc;
	s7_pointer lcur;
      } lc;
      union {
	s7_int len;
	s7_pointer slow;
	hash_entry_t *hcur;
      } lw;
      s7_pointer (*next)(s7_scheme *sc, s7_pointer iterator);
    } iter;

    struct {
      c_proc_t *c_proc;              /* C functions, macros */
      s7_function ff;
      s7_pointer setter;
      uint32_t required_args, optional_args, all_args;
      bool rest_arg;
    } fnc;

    struct {                         /* pairs */
      s7_pointer car, cdr, opt1, opt2, opt3;
    } cons;

    struct {                         /* pairs */
      s7_pointer car, cdr, opt1, opt2;
      uint8_t opt_type;
    } cons_ext;

    struct {                         /* special purpose pairs (symbol-table etc) */
      s7_pointer unused_car, unused_cdr;
      uint64_t hash;
      const char *fstr;
      uint32_t op, line;             /* op=optimize_op, line=pair_line or saved symbol_ctr */
    } sym_cons;

    struct {                        /* scheme functions */
      s7_pointer args, body, env, setter;  /* args can be a symbol, as well as a list */
      int32_t arity, opt_addr;
    } func;

    struct {                        /* optlist (internal) */
      int32_t num_exprs, num_args, len, addr, pc;
      opt_info *opts;
    } opt;

    struct {                        /* strings */
      uint32_t length;
      union {
	bool needs_free;            /* string GC */
	uint32_t setter;            /* symbol-setter address (for a global symbol = symbol_global_setter_index) */
	int32_t temp_len;           /* temp string length (sc->tmp_strs) */
      } str_ext;
      char *svalue;
      uint64_t hash;                /* string hash-index */
      union {
	char *documentation;        /* symbol help */
	s7_pointer ksym;            /* keyword->symbol */
      } doc;
      symbol_info_t *info;
    } string;

    struct {                       /* symbols */
      s7_pointer name, global_slot, local_slot;
      int64_t id;
      uint32_t ctr;
      symbol_tag_t tag;
    } sym;

    struct {                       /* syntax */
      s7_pointer symbol;
      opcode_t op;
      int16_t min_args, max_args;
    } syn;

    struct {                       /* slots (bindings) */
      s7_pointer sym, val, nxt, pending_value, expr;
    } slt;

    struct {                       /* environments (frames, lets) */
      s7_pointer slots, nxt;
      int64_t id;                  /* id of rootlet is -1 */
      union {
	struct {
	  s7_pointer function;     /* __func__ (code) if this is a funclet */
	  uint32_t line, file; /* __func__ location if it is known */
	} efnc;
	struct {
	  s7_pointer dox1, dox2;   /* do loop variables */
	} dox;
	struct {                   /* (catch #t ...) opts */
	  uint64_t op_stack_loc, goto_loc;
	} ctall;
      } edat;
    } envr;

    struct {                        /* special stuff like #<unspecified> */
      /* these 3 are just place-holders */
      s7_pointer unused_slots, unused_nxt;
      int64_t unused_id;
      /* these two fields are for some special case objects like #<unspecified> */
      const char *name;
      int32_t len;
    } unq;

    struct {                        /* counter (internal) */
      s7_pointer result, list, env, slots; /* env = counter_let (curlet after map/for-each frame created) */
      uint64_t cap;                 /* sc->capture_let_counter for frame reuse */
    } ctr;

    struct {                        /* random-state */
#if WITH_GMP
      gmp_randstate_t state;
#else
      uint64_t seed, carry;
#endif
    } rng;

    struct {                        /* additional object types (C) */
      int32_t type;
      void *value;                  /*  the value the caller associates with the c_object */
      s7_pointer e;                 /*   the method list, if any (openlet) */
      void (*mark)(void *val);
    } c_obj;

    struct {                        /* continuations */
      continuation_t *continuation;
      s7_pointer stack;
      s7_pointer *stack_start, *stack_end, *op_stack;
    } cwcc;

    struct {                        /* call-with-exit */
      uint64_t goto_loc, op_stack_loc;
      bool active;
    } rexit;

    struct {                        /* catch */
      uint64_t goto_loc, op_stack_loc;
      s7_pointer tag;
      s7_pointer handler;
    } rcatch; /* C++ reserves "catch" I guess */

    struct {                        /* dynamic-wind */
      s7_pointer in, out, body;
      uint32_t state;
    } winder;
  } object;

#if WITH_PROFILE
  int32_t file_and_line;
#endif
#if DEBUGGING
  int32_t current_alloc_line, previous_alloc_line, gc_line, alloc_line, uses, explicit_free_line;
  int64_t current_alloc_type, previous_alloc_type, debugger_bits;
  const char *current_alloc_func, *previous_alloc_func, *gc_func, *alloc_func;
#endif

} s7_cell;


