/* -------------------------------- GC -------------------------------- */

/* in most code, pairs, lets, and slots dominate the heap -- each about 25% to 40% of the
 *   total cell allocations.  In snd-test, reals are 50%. slots need not be in the heap,
 *   but moving them out to their own free list was actually slower because we need (in that
 *   case) to manage them in the sweep process by tracking lets.  
 */

#if DEBUGGING
static uint32_t s7_gc_protect_2(s7_scheme *sc, s7_pointer x, int line)
{
  uint32_t loc;
  loc = s7_gc_protect(sc, x);
  sc->protected_lines[loc] = line;
  return(loc);
}
#define s7_gc_protect_1(Sc, X) s7_gc_protect_2(Sc, X, __LINE__)
#else
#define s7_gc_protect_1(Sc, X) s7_gc_protect(Sc, X)
#endif

uint32_t s7_gc_protect(s7_scheme *sc, s7_pointer x)
{
  uint32_t loc;

  if (sc->gpofl_loc < 0)
    {
      uint32_t i, size, new_size;
      size = sc->protected_objects_size;
      new_size = 2 * size;
      vector_elements(sc->protected_objects) = (s7_pointer *)realloc(vector_elements(sc->protected_objects), new_size * sizeof(s7_pointer));
      vector_length(sc->protected_objects) = new_size;
      sc->protected_objects_size = new_size;
      sc->gpofl = (uint32_t *)realloc(sc->gpofl, new_size * sizeof(uint32_t));
#if DEBUGGING
      sc->protected_lines = (int *)realloc(sc->protected_lines, new_size * sizeof(int));
      for (i = size; i < new_size; i++) sc->protected_lines[i] = -1;
      {
	int *protected_count;
	protected_count = (int *)calloc(85000, sizeof(int));
	for (i = 0; i < size; i++)
	  if ((sc->protected_lines[i] >= 0) &&
	      (vector_element(sc->protected_objects, i) != sc->gc_nil))
	    protected_count[sc->protected_lines[i]]++;
	for (i = 0; i < 85000; i++)
	  if (protected_count[i] > 0)
	    fprintf(stderr, "line[%d]: %d\n", i, protected_count[i]);
	free(protected_count);
      }
#endif
      for (i = size; i < new_size; i++)
	{
	  vector_element(sc->protected_objects, i) = sc->gc_nil;
	  sc->gpofl[++sc->gpofl_loc] = i;
	}
    }

  loc = sc->gpofl[sc->gpofl_loc--];
#if DEBUGGING
  if (loc >= sc->protected_objects_size)
    fprintf(stderr, "sc->gpofl loc: %u (%d)\n", loc, sc->protected_objects_size);
  if (vector_element(sc->protected_objects, loc) != sc->gc_nil)
    fprintf(stderr, "protected object at %u about to be clobbered? %s\n", loc, DISPLAY(vector_element(sc->protected_objects, loc)));
  sc->protected_lines[loc] = 1;
#endif
  vector_element(sc->protected_objects, loc) = x;
  return(loc);
}

void s7_gc_unprotect(s7_scheme *sc, s7_pointer x)
{
  uint32_t i;
  for (i = 0; i < sc->protected_objects_size; i++)
    if (vector_element(sc->protected_objects, i) == x)
      {
	vector_element(sc->protected_objects, i) = sc->gc_nil;
	sc->gpofl[++sc->gpofl_loc] = i;
	return;
      }
}

void s7_gc_unprotect_at(s7_scheme *sc, uint32_t loc)
{
  if (loc < sc->protected_objects_size)
    {
      if (vector_element(sc->protected_objects, loc) != sc->gc_nil)
	sc->gpofl[++sc->gpofl_loc] = loc;
      vector_element(sc->protected_objects, loc) = sc->gc_nil;
    }
}

s7_pointer s7_gc_protected_at(s7_scheme *sc, uint32_t loc)
{
  s7_pointer obj;

  obj = sc->unspecified;
  if (loc < sc->protected_objects_size)
    obj = vector_element(sc->protected_objects, loc);
  if (obj == sc->gc_nil)
    return(sc->unspecified);

  return(obj);
}

#define gc_protected_at(Sc, Loc) vector_element(Sc->protected_objects, Loc)


static void (*mark_function[NUM_TYPES])(s7_pointer p);

#define S7_MARK(Obj) do {s7_pointer _p_; _p_ = Obj; if (!is_marked(_p_)) (*mark_function[unchecked_type(_p_)])(_p_);} while (0)

static void mark_slot(s7_pointer p)
{
  set_mark(p);
  S7_MARK(slot_value(p));
  if (slot_has_setter(p))
    S7_MARK(slot_setter(p));

  /* if (is_gensym(slot_symbol(p))) */ /* (let () (apply define (gensym) (list 32)) (gc) (gc) (curlet)) */
  set_mark(slot_symbol(p));
}

static void mark_symbol(s7_pointer p)
{
  /* if (is_gensym(p)) */
  set_mark(p);
}

static void mark_noop(s7_pointer p) {}


/* ports can be alloc'd and freed at a blistering pace, so I think I'll make a special free-list for them. */

static port_t *alloc_port(s7_scheme *sc)
{
  if (sc->port_heap)
    {
      port_t *p;
      p = sc->port_heap;
      sc->port_heap = (port_t *)(p->next);
      return(p);
    }
  return((port_t *)calloc(1, sizeof(port_t)));
}

static void free_port(s7_scheme *sc, port_t *p)
{
  p->next = (void *)(sc->port_heap);
  sc->port_heap = p;
}


#define STRING_LISTS 256
#define STRING_LIST_INIT_SIZE 2

static void init_string_free_lists(s7_scheme *sc)
{
  int32_t i;
  sc->string_lists = (char ***)calloc(STRING_LISTS, sizeof(char **));
  sc->string_locs = (int32_t *)calloc(STRING_LISTS, sizeof(int32_t));
  sc->string_sizes = (int32_t *)malloc(STRING_LISTS * sizeof(int32_t));
  sc->string_max_sizes = (int32_t *)malloc(STRING_LISTS * sizeof(int32_t));
  for (i = 0; i < STRING_LISTS; i++)
    {
      sc->string_lists[i] = (char **)calloc(STRING_LIST_INIT_SIZE, sizeof(char *));
      sc->string_sizes[i] = STRING_LIST_INIT_SIZE;
      if (i < 16)
	sc->string_max_sizes[i] = 4096;
      else
	{
	  if (i < 32)
	    sc->string_max_sizes[i] = 1024;
	  else 
	    {
	      if (i < 64)
		sc->string_max_sizes[i] = 256;
	      else sc->string_max_sizes[i] = 32;
	    }
	}
    }
}

static char *alloc_string(s7_scheme *sc, int32_t len)
{
  if ((len < STRING_LISTS) &&
      (sc->string_locs[len] > 0))
    return(sc->string_lists[len][--sc->string_locs[len]]);
  return((char *)malloc((len + 1) * sizeof(char)));
}

static void string_to_free_list(s7_scheme *sc, char *value, int32_t len)
{
  if (len >= STRING_LISTS)
    free(value);
  else
    {
      if (sc->string_locs[len] >= sc->string_sizes[len])
	{
	  if (sc->string_sizes[len] >= sc->string_max_sizes[len])
	    {
	      free(value);
	      return;
	    }
	  sc->string_sizes[len] *= 2;
	  sc->string_lists[len] = (char **)realloc((void *)(sc->string_lists[len]), sc->string_sizes[len] * sizeof(char *));
	}
      sc->string_lists[len][sc->string_locs[len]++] = value;
    }
}

static void close_output_port(s7_scheme *sc, s7_pointer p);
static void free_optlist(s7_scheme *sc, s7_pointer p);

static void sweep(s7_scheme *sc)
{
  uint32_t i, j;
  s7_pointer s1;
  gc_list *gp;
  
  gp = sc->strings;
  if (gp->loc > 0)
    {
      /* unrolling this loop is not an improvement */
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      if (string_needs_free(s1))
		string_to_free_list(sc, string_value(s1), string_length(s1));
	    }
	  else 
	    {
	      /* remove_from_heap can remove a string from the heap; we need to notice that removal
	       *   via in_heap, and remove it also from this cache; otherwise it just stays here
	       *   forever, slowing down the loop. 
	       */
	      if (in_heap(s1)) /* this costs more than it saves */
		gp->list[j++] = s1;
	    }
	}
      gp->loc = j;
    }

  gp = sc->strings1;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      if (string_needs_free(s1))
		free(string_value(s1));
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->gensyms;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      /* free(symbol_info(s1)); */
	      remove_gensym_from_symbol_table(sc, s1); /* this uses symbol_name_cell data */
	      free(symbol_name(s1));
	      free(symbol_name_cell(s1));
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
      if (j == 0) mark_function[T_SYMBOL] = mark_noop;
    }

  gp = sc->c_objects;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    (*(c_object_free(sc, s1)))(c_object_value(s1));    
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->optlists;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    free_optlist(sc, s1);
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->vectors;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      /* a multidimensional empty vector can have dimension info, wrapped vectors always have dimension info */
	      if (vector_dimension_info(s1))
		{
		  if (vector_dimensions_allocated(s1))
		    {
		      free(vector_dimensions(s1));
		      free(vector_offsets(s1));
		    }
		  if (vector_elements_allocated(s1))
		    free(vector_elements(s1));      /* I think this will work for any vector (int/float too) */
		  if (vector_dimension_info(s1) != sc->wrap_only)
		    free(vector_dimension_info(s1));
		}
	      else
		{
		  if (vector_length(s1) != 0)
		    free(vector_elements(s1));
		}
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->hash_tables;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      if (hash_table_mask(s1) > 0)
		free_hash_table(s1);
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->input_ports;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      if (!port_is_closed(s1))
		{
		  if (is_file_port(s1))
		    {
		      if (port_file(s1))
			{
			  fclose(port_file(s1));
			  port_file(s1) = NULL;
			}
		    }
		  else
		    {
		      if ((is_string_port(s1)) &&
			  (port_needs_unprotect(s1)))
			{
			  s7_gc_unprotect_at(sc, port_gc_loc(s1));
			  port_needs_unprotect(s1) = false;
			}
		    }
		}
	      if (port_needs_free(s1))
		{
		  if (port_data(s1))
		    {
		      free(port_data(s1));
		      port_data(s1) = NULL;
		      port_data_size(s1) = 0;
		    }
		  port_needs_free(s1) = false;
		}

	      if (port_filename(s1))
		{
		  free(port_filename(s1));
		  port_filename(s1) = NULL;
		}
	      free_port(sc, port_port(s1));
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->output_ports;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      close_output_port(sc, s1); /* needed for free filename, etc */
	      free_port(sc, port_port(s1));
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->continuations;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    {
	      if (continuation_op_stack(s1))
		{
		  free(continuation_op_stack(s1));
		  continuation_op_stack(s1) = NULL;
		}
	      free(continuation_data(s1));
	    }
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

#if WITH_GMP
  gp = sc->bigints;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s7_pointer s1;
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    mpz_clear(big_integer(s1));
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->bigratios;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s7_pointer s1;
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    mpq_clear(big_ratio(s1));
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->bigreals;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s7_pointer s1;
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    mpfr_clear(big_real(s1));
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }

  gp = sc->bignumbers;
  if (gp->loc > 0)
    {
      for (i = 0, j = 0; i < gp->loc; i++)
	{
	  s7_pointer s1;
	  s1 = gp->list[i];
	  if (is_free_and_clear(s1))
	    mpc_clear(big_complex(s1));
	  else gp->list[j++] = s1;
	}
      gp->loc = j;
    }
#endif
}

static inline void add_to_gc_list(gc_list *gp, s7_pointer p)
{
  if (gp->loc == gp->size)
    {
      gp->size *= 2;
      gp->list = (s7_pointer *)realloc(gp->list, gp->size * sizeof(s7_pointer));
    }
  gp->list[gp->loc++] = p;
}

static gc_list *make_gc_list(void)
{
  gc_list *gp;
  #define INIT_GC_CACHE_SIZE 4
  gp = (gc_list *)malloc(sizeof(gc_list));
  gp->size = INIT_GC_CACHE_SIZE;
  gp->loc = 0;
  gp->list = (s7_pointer *)malloc(gp->size * sizeof(s7_pointer));
  return(gp);			 
}

static void add_gensym(s7_scheme *sc, s7_pointer p)
{
  add_to_gc_list(sc->gensyms, p);
  mark_function[T_SYMBOL] = mark_symbol;
}


