static bool opt_int_not_pair(s7_scheme *sc, s7_pointer car_x)
{
  opt_info *opc;
  if (is_opt_int(car_x))
    {
      opc = alloc_opo(sc, car_x);
      opc->v1.i = integer(car_x);
      opc->v7.fi = opt_i_c;
      return(true);
    }
  if (is_symbol(car_x))
    {
      s7_pointer p;
      p = find_symbol(sc, car_x);
      if ((is_slot(p)) &&
	  (is_opt_int(slot_value(p))))
	{
	  opc = alloc_opo(sc, car_x);
	  opc->v1.p = p;
	  opc->v7.fi = opt_i_s;
	  return(true);
	}
    }
  return(return_false(sc, car_x, __func__, __LINE__));
}

