		  goto OPT_EVAL_CHECKED;
		  
		case OP_SAFE_C_ZS:
		  if (!c_function_is_ok(sc, code)) break;
		case HOP_SAFE_C_ZS:
		  check_stack_size(sc);
		  push_stack_no_args(sc, OP_EVAL_ARGS_P_3, code);
		  sc->code = _TPair(cadr(code));
		  goto OPT_EVAL_CHECKED;
		  
		case OP_SAFE_C_opAq:
		  if (!c_function_is_ok(sc, code)) break;
		case HOP_SAFE_C_opAq:
